select 'create tablespace '||a.tablespace_name||' datafile '''||a.file_name||''' size '||a.bytes/1024/1024||'M default storage (initial '||b.initial_extent||' next '||next_extent||' minextents '||min_extents||' maxextents '||max_extents||' pctincrease '||pct_increase||');' from dba_data_files a, dba_tablespaces b where a.tablespace_name = b.tablespace_name and a.tablespace_name not in ('SYSTEM','RBS','ROLLBACK') and b.contents != 'TEMPORARY'
